import React, { Component } from 'react'
import News from './News'
import axios from 'axios'

export default class NewsAPi extends Component {
    constructor(props){
        super(props)
        this.state={
            value: false
        }
    }
    
    componentDidMount(){

        if(this.state.value===false){
            axios.get('/news',{responseType: 'json' })
            
            .then(response=>response.data)
            .then(data=>{
                 data.forEach(el=>{
                    let date=el.date;
                    let img=el.img||null;
                    let title=el.title;
                    let text=el.text;
                    this.props.all(date,img,title,text);
                }
                
            )})
            this.setState(true);
        }
       
    }
    
    render() {
        return <News allNews={this.props.allNews}/>
    }
}
