import axios from 'axios'
import React, { useEffect, useState } from 'react'
import NewNewsContainer from './NewNews/NewNewsContainer'
import styles from './News.module.css'
import OneNews from './OneNews/OneNews'

export default function News({allNews}) {
    let[value ,setValue]=useState(false)
    let[text,setText]=useState('');
    let open=()=>{
        setValue(true)
    }
    let close=()=>{
        setValue(false)
    }
    useEffect(()=>{
        fetch('/news',"GET")
        .then(response=>{
            setText(response)
        })
        .catch(()=>{setText('Нет ответа')})
    })

    
    return (
        <div className={styles.wap}>
            <h1>{text}</h1>
        <button onClick={open} className={styles.button}>Добвить Новость</button>
        
        {value===true? <NewNewsContainer close={close} />:null }
        <div className={styles.news}> 
            {allNews.map(el=><OneNews news={el}/>)}
        </div>
        </div>
    )
}
